const burger=document.querySelector(".header__burger"),headerMenu=document.querySelector(".header-menu"),heroIcons=document.querySelector(".hero-icons"),slider=document.querySelector(".blog-slider__container"),leftBtn=document.querySelector("[data-slider-left]"),rightBtn=document.querySelector("[data-slider-right]");burger.addEventListener("click",e=>{burger.classList.toggle("_active"),headerMenu.classList.toggle("_opened")}),headerMenu.addEventListener("click",e=>{e.target.closest(".header-menu__nav")||(headerMenu.classList.remove("_opened"),burger.classList.remove("_active"))});let wholeSliderOffset=0,slideOffset=document.querySelector(".slide").offsetWidth+70;const sliderLength=document.querySelectorAll(".slide").length;function moveSlider(e){slider.style.transform=`translateX(${e}px)`}function setStateOfBtns(){wholeSliderOffset<0?leftBtn.disabled=!1:leftBtn.disabled=!0,wholeSliderOffset===-slideOffset*(sliderLength-1)?rightBtn.disabled=!0:rightBtn.disabled=!1}leftBtn.addEventListener("click",e=>{leftBtn.disabled||(wholeSliderOffset+=slideOffset*+leftBtn.dataset.sliderLeft,moveSlider(wholeSliderOffset),setStateOfBtns())}),rightBtn.addEventListener("click",e=>{rightBtn.disabled||(wholeSliderOffset+=slideOffset*+rightBtn.dataset.sliderRight,moveSlider(wholeSliderOffset),setStateOfBtns())}),window.addEventListener("scroll",e=>{heroIcons.style.bottom=window.pageYOffset/4-81+"px"});